import styled, { css } from 'styled-components';

interface IWrapper {
    generated?: boolean;
}

export const Wrapper = styled.div<IWrapper>`
    width: 100%;
    height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    ${({ generated }) => generated && css`
        /* margin: 50px 0; */
    `}

    img {
        margin-top: 30px;
    }

    @media(max-width: 340px) {
        img {
            width: 80%;
        }
    }
`;

export const Meme = styled.img`
    height: 400px;

    @media(max-width: 1000px) {
        height: 200px;
        width: 100%;
    }
`;

export const Card = styled.div`
    background: #fff;
    width: 550px;
    border-radius: 8px;
    padding: 20px;
    margin: 20px 0;
    box-shadow: 0 6px 10px 0 rgba(0, 0, 0, 0.20);

    h2 {
        font-size: 22px;
        color: #392d2d;
        margin-bottom: 10px;
    }

    @media(max-width: 1000px) {
        width: 90%;
    }
`;

export const Templates = styled.div`
    width: 100%;
    height: 90px;
    background: #eee;
    border-radius: 8px;
    overflow-y: auto;
    display: flex;
    align-items: center;
    padding: 0 15px;
    margin-bottom: 30px;

    button {
        background: transparent;
        margin-right: 10px;
        border: 2px solid transparent;

        &.selected {
            border-color: #4395d8;
        }

        img {
            width: 53px;
            height: 53px;
        }
    }
`;

export const Form = styled.form`
    input {
        width: 100%;
        height: 40px;
        border-radius: 8px;
        border: 1px solid #dbdbdb;
        padding: 0 15px;
        font-size: 14px;
        margin-bottom: 10px;
    }
`;

export const Button = styled.button`
    width: 100%;
    height: 40px;
    border-radius: 8px;
    border: 0;
    background: #4395d8;
    color: #fff;
    font-weight: bold;
    transition: background 0.2s ease-in;

    &:hover {
        background: #3672a3;
    }

    & + & {
        margin-top: 20px;
    }
`;
