import React, { FC, useState, useEffect } from 'react';
import { Wrapper, Card, Templates, Form, Button } from './styles';

import logo from '../../assets/logo.svg';
import api from '../../services/api';

interface IProps {
    data: IData;
}

interface IData {
    memes: ITemplate[];
}

interface ITemplate {
    id: number;
    name: string;
    url: string;
    box_count: number;
}

const Home: FC = () => {
    const [templates, setTemplates] = useState<ITemplate[]>([]);
    const [selectedTemplate, setSelectedTemplate] = useState<ITemplate>();

    useEffect(() => {
        (async () => {
            const response = await api.get('https://api.imgflip.com/get_memes');           
            const { data: { memes } } = response.data;

            setTemplates(memes);
        })();
    }, [])

    return (
        <>
            <Wrapper>
                <img src={ logo } alt="MemeMaker"/>

                <Card>
                    <h2>Selecione um template</h2>

                    <Templates>
                        { templates.map((template) => (
                            <button key={ template.id } onClick={ () => setSelectedTemplate(template) } className={ template.id == selectedTemplate?.id ? 'selected' : '' }>
                                <img src={ template.url } alt={ template.name } />
                            </button>
                        ))}
                    </Templates>

                    { selectedTemplate && (
                        <>
                            <h2>Textos</h2>

                            <Form>
                                { (new Array(selectedTemplate.box_count).fill('').map((_, index) => (
                                    <input type="text" key={index} placeholder={ `Text #${index + 1}`} />
                                )))}

                                <Button type="submit">MakeMyMeme</Button>
                            </Form>
                        </>
                    )}

                </Card>
            </Wrapper>
        </>
    );
};

export default Home;
